function datafilter(obj,query,properties){for(let i=0;i<properties.length;i++)if(obj[properties[i]]&&obj[properties[i]].toLowerCase().includes(query.toLowerCase()))return!0;return!1}function dataTableValues(){return{limit:"10",page:0,maxpage:0,from:1,to:10,q:""}}function dataTable(listado,obj,way,...properties){if(0==way){obj.temp=[];for(let i=0;i<listado.length;i++)datafilter(listado[i],obj.q,properties)&&obj.temp.push(listado[i]);obj.maxpage=obj.temp.length%obj.limit==0?obj.temp.length/obj.limit:Math.ceil(obj.temp.length/obj.limit)}else{if(obj.page+way<0||obj.page+way>=obj.maxpage)return{page:obj.page,maxpage:obj.maxpage,from:obj.from,to:obj.to,limit:obj.limit,temp:obj.temp};obj.page+=way,obj.from=0==obj.page?1:obj.page*obj.limit+1,obj.to=obj.from+parseInt(obj.limit)-1}obj.to=obj.temp.length<obj.to?obj.temp.length:obj.limit*(obj.page+1),obj.from=obj.from<obj.to?obj.from:obj.to<obj.limit?1:obj.to/obj.limit*obj.limit+1;let sublist=obj.temp.slice(obj.from-1,obj.to);return{page:obj.page,maxpage:obj.maxpage,from:obj.from,to:obj.to,limit:obj.limit,temp:obj.temp,sublist:sublist}}